import React, { Component } from 'react'
import './myStyle.css';

class Linear extends Component {
    render(){
        let flag=false;
        const ui=this.props.ar.map((x,index)=>
        {
            if(this.props.index===index)
            {
                if(this.props.ar[this.props.index]!=this.props.srch)
                return(<div key={index} className="red">{x}</div>);
                else
                {
                    return(<div key={index} className="green" onLoad={this.props.check}>{x}</div>);
                    flag=true;
                }
            }
            else
            return(<div key={index} className="q">{x}</div>);
        });
        return (
            <center>
            <div className="mystl">
                {ui}
            </div>
            </center>
        )
    }
}

export default Linear
