import React, { Component } from 'react'
import Linear from './linear';
import './myStyle.css';

class LinearParent extends Component {
    constructor(props) {
        super(props);
        this.state = {
             index:0,
             start:0,
             flag:false
        }
        this.check=this.check.bind(this);
    }
    check=()=>{
        this.setState({
            flag:true
        });
    }
    shouldComponentUpdate(nextProps,nextState){
        if(this.state.flag)
        return false;
        return true;
    }
    linearSearch(){
        if(this.state.index==4)
        return;
        if(this.state.flag==true)
        return;
        console.log(`${this.state.flag} ${this.state.index}`);
        setTimeout(() => {
            this.setState(prev=>({
                index:prev.index+1,
                start:1
            }))
        }, 1000);
    }
    render() {
        const arr=[3,5,4,1,6];
        const srch=9;
        if(this.state.start==1)
        return (
            <div>
                <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                <button onClick={this.linearSearch()}>Run</button>
            </div>
        )
        else{
            return (
                <div>
                    <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                    <button onClick={()=>this.linearSearch()}>Run</button>
                </div>
            )
        }
    }
}

export default LinearParent
