import React, { Component } from 'react'
import './myStyle.css';

class Binary extends Component {
    render() {
      //  const {l,r,ar,srch}=this.props;
        const ui=this.props.ar.map((x,index)=>{
            let mid=(this.props.l+this.props.r)/2;
            if(mid==index)
            {
                if(this.props.ar[mid]===this.props.srch)
                    return(<div key={index} className="def green col-2 col-sm-2 col-md-1 col-1 align-self-center">{x}</div>);
                else
                    return(<div key={index} className="def red col-2 col-sm-2 col-md-1 col-1 align-self-center">{x}</div>);
            }
            else
            {
                if(index==this.props.l)
                return(<div key={index} className="def lblue col-2 col-sm-2 col-md-1 col-1 align-self-center">{x}</div>);
                else if(index==this.props.r)
                return(<div key={index} className="def lblue col-2 col-sm-2 col-md-1 col-1 align-self-center">{x}</div>);
                else
                return(<div key={index} className="def green col-2 col-sm-2 col-md-1 col-1 align-self-center">{x}</div>);
            }
        });
        return (
            <center>
            <div className="mystl container">
                <div className="row">
                    {ui}
                </div>
            </div>
            </center>
        )
    }
}

export default Binary
