import React, { Component } from 'react'
import "./myStyle.css";

class Binary extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            index:null,
            ar:[3,5,6,8,9,10]
        }
    }
    linearSearch(){
        const srch=8;
        for(let i=0;i<=5;i++){
            this.setState({
                index:i
            });
        }
    }

    render() {
        const ui=this.state.ar.map((x,index)=>
        {
            if(this.state.index==index)
            <div key={index} className="red">{x}</div>
            else
            <div key={index} className="q">{x}</div>
        });
        return (
            <center>
            <div className="mystl">
                {ui}
            </div>
            <button onClick={()=>this.linearSearch()}>Run</button>
            </center>
        )
    }
}

export default Binary
