import React, { Component } from 'react'
import Linear from './linear';
import './myStyle.css';

class LinearParent extends Component {
    constructor(props) {
        super(props);
        this.state = {
             index:0,
             start:0,
             flag:false,
             resetflag:false
        }
        this.check=this.check.bind(this);
    }
    check=()=>{
        this.setState({
            flag:true
        });
    }
    shouldComponentUpdate(nextProps,nextState){
        console.log("shouldComponentUpdate "+this.state.resetflag);
        if(this.state.flag==true&&this.state.resetflag==false)
        return false;
        return true;
    }
    linearSearch(){
        if(this.state.index==4)
        return;
        if(this.state.flag==true)
        return;
//        console.log(`${this.state.flag} ${this.state.index}`);
        setTimeout(() => {
            this.setState(prev=>({
                index:prev.index+1,
                start:1
            }))
        }, 400);
    }
    reset(){
        console.log("reset");
        this.setState({
            flag:false,
            index: 0,
            start:0,
            resetflag:true
        },function(){
            this.render();
            console.log("done");
        });
    }
    render() {
        console.log("render() "+this.state.flag);
        const arr=[3,5,4,1,6];
        const srch=1;
        if(this.state.start==1)
        return (
            <div>
                <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                <button onClick={this.linearSearch()}>Run</button>
                <button type='submit' onClick={()=>this.reset()}>Reset</button>
            </div>
        )
        else{
            return (
                <div>
                    <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                    <button onClick={()=>this.linearSearch()}>Run</button>
                    <button type='submit' onClick={()=>this.reset()}>Reset</button>
                </div>
            )
        }
    }
}

export default LinearParent
