import React, { Component } from 'react'
import Linear from './linear';
import './myStyle.css';

class LinearParent extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             index:0
        }
    }
    linearSearch(){
        setTimeout(() => {
            this.setState(prev=>({
                index:prev.index+1
            }))
        }, 400);
    }
    render() {
        const arr=[3,5,4,1,6];
        return (
            <div>
                <Linear ar={arr} index={this.state.index}/>
                <button onClick={()=>this.linearSearch()}>Run</button>
            </div>
        )
    }
}

export default LinearParent
