import React, { Component } from 'react'
import Binary from './binary';
import './myStyle.css';

const arr=['4','5','53','66','96','100','200'];
const n=arr.length;

class BinaryParent extends Component {
    constructor(props) {
        super(props)
        this.state = {
             l:0,
             r:n-1,
             start:0,
             search:'96',
             flag:false,
             less:false
        }
    }
    
    check=()=>{
        this.setState({
            flag:true
        })
    }
    lesser=()=>{
        this.setState({
            less:true
        })
    }

    shouldComponentUpdate(nextProps,nextState){
            if(this.state.flag)
            return false;
            return true;
    }

    binarySearch(){
        if(this.state.l>this.state.r)
        return;
        if(this.state.flag==true)
        return;
        setTimeout(() => {
            let mid=Math.floor((this.state.l+this.state.r)/2);
            if(this.state.less)
            {
                this.setState(prev=>({
                    l:mid+1,
                    less:false,
                    start:1
                }))
            }
            else{
                this.setState(prev=>({
                    r:mid-1,
                    start:1
                }))
            }
        }, 400);
    }

    render() {
        if(this.state.start==1)
        return (
            <center>
            <div>
                <Binary l={this.state.l} r={this.state.r} ar={arr} srch={this.search} lesser={this.lesser} check={this.state.check} />
                <button className="button button4" onClick={this.binarySearch()}>Run</button>
            </div>
            </center>
        )
        else
        return (
            <center>
            <div>
                <Binary l={this.state.l} r={this.state.r} ar={arr} srch={this.search} lesser={this.lesser} check={this.state.check}/>
                <button className="button button4" onClick={()=>this.binarySearch()}>Run</button>
            </div>
            </center>
        )
    }
}

export default BinaryParent
