import React, { Component } from 'react'
import Linear from './linear';
import './myStyle.css';

class LinearParent extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             index:0,
             start:0
        }
    }
    linearSearch(){
        if(this.state.index==3)
        return;
        setTimeout(() => {
            this.setState(prev=>({
                index:prev.index+1,
                start:1
            }))
        }, 1000);
    }
    render() {
        const arr=[3,5,4,1,6];
        if(this.state.start==1)
        return (
            <div>
                <Linear ar={arr} index={this.state.index}/>
                <button onClick={this.linearSearch()}>Run</button>
            </div>
        )
        else{
            return (
                <div>
                    <Linear ar={arr} index={this.state.index}/>
                    <button onClick={()=>this.linearSearch()}>Run</button>
                </div>
            )
        }
    }
}

export default LinearParent
