import React, { Component } from 'react'
import Linear from './linear';
import './myStyle.css';

const arr=[3,5,4,1,6];
        const n=arr.length();
        const srch=1;
class LinearParent extends Component {
    constructor(props) {
        super(props);
        this.state = {
             index:0,
             start:0,
             flag:false
        }
        this.check=this.check.bind(this);
    }
    check=()=>{
        this.setState({
            flag:true
        });
    }
    shouldComponentUpdate(nextProps,nextState){
//        console.log("shouldComponentUpdate "+this.state.flag);
        if(this.state.flag)
        return false;
        return true;
    }
    linearSearch(){
        if(this.state.index===n-1)
        return;
        if(this.state.flag===true)
        return;
//        console.log(`${this.state.flag} ${this.state.index}`);
        setTimeout(() => {
            this.setState(prev=>({
                index:prev.index+1,
                start:1
            }))
        }, 400);
    }
    reset(){
//        console.log("reset");
        this.setState({
            flag:false,
            index: 0,
            start:0
        },function(){
            this.forceUpdate()
        });
    }
    render() {
//        console.log("render() "+this.state.start);
        if(this.state.start===1)
        return (
            <div>
                <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                <button className="button button4" onClick={this.linearSearch()}>Run</button>
                <button className="button button4" type='submit' onClick={()=>this.reset()}>Reset</button>
                <input type='text'></input>
            </div>
        )
        else{
            return (
                <div>
                    <Linear ar={arr} index={this.state.index} srch={srch} check={this.check}/>
                    <button className="button button4" onClick={()=>this.linearSearch()}>Run</button>
                    <button className="button button4" type='submit' onClick={()=>this.reset()}>Reset</button>
                    <input type='text' className='txt txt2'></input>
                </div>
            )
        }
    }
}

export default LinearParent
