    constructor(props) {
        super(props);
//        this.clickHandler=this.clickHandler.bind(this);
        this.state={
            message:"Welcome"
        }
    }
    clickHandler(){
        this.setState({
           message:"Good Bye" 
        });
        console.log(this);
    }
//clickHandler=()=>{
